    uint256 public constant decimals = 18;

    /**
     * CONSTRUCTOR 
     * 
     */
    function NewIntelTechMedia(address _owner) 
        public 
        {
        totalSupply = 500000000000000000000000000;
        owner = _owner;
        paused = false;

        balances[owner] = totalSupply;
        Transfer(address(0), owner, totalSupply);

    }
