/**
 * The PlatformX Token (PX) has a fixed supply and is not mintable.
 */
contract PXToken is StandardToken, BurnableToken, Ownable {

    // Constants
    string  public constant name = "PlatformX Token";
    string  public constant symbol = "PX";
    uint8   public constant decimals = 18;
    uint256 public constant INITIAL_SUPPLY      = 5000000000 * (10 ** uint256(decimals));


    constructor() public {

        totalSupply = INITIAL_SUPPLY;
        balances[msg.sender] = totalSupply;
        emit Transfer(address(0x0), msg.sender, totalSupply);

    }

    function transfer(address _to, uint256 _value) public returns (bool) {
        return super.transfer(_to, _value);
    }

    function burn(uint256 _value) public {
        require(msg.sender == owner);
        require(balances[msg.sender] >= _value);
        super.burn(_value);
        emit Transfer(msg.sender, address(0x0), _value);
    }
}
