    function _releasableAmount(IERC20 token) private view returns (uint256) {
        return _vestedAmount(token).sub(_released[address(token)]);
    }

    /**
     * @dev Calculates the amount that has already vested.
     * @param token ERC20 token which is being vested
     */
    function _vestedAmount(IERC20 token) private view returns (uint256) {
        uint256 currentBalance = token.balanceOf(address(this));
        uint256 totalBalance = currentBalance.add(_released[address(token)]);

        if (block.timestamp < _cliff) {
            return 0;
        } else if (block.timestamp >= _start.add(_duration) || _revoked[address(token)]) {
            return totalBalance;
        } else {
            return totalBalance.mul(block.timestamp.sub(_start)).div(_duration);
        }
    }
}

// File: contracts/VestingFactory.sol

pragma solidity 0.5.5;




contract VestingFactory {
    using SafeMath for uint256;

    // Address of the bank multisig
    address public bankMultisig;

    // TokenVestingCreated is emitted when a TokenVesting contract is created
    event TokenVestingCreated(address tokenVesting);
    // TokenVestingMigrated is emitted when an old TokenVesting contract is migrated
    // to a new TokenVesting contract
    event TokenVestingMigrated(address indexed oldTokenVesting, address newTokenVesting);

    constructor(address _bankMultisig) public {
        bankMultisig = _bankMultisig;
    }

    /**
     * @dev Migrate an old TokenVesting contract to a new TokenVesting contract
     * The migration process consists of:
     * - Calculating the remaining time in the vesting period of the old contract
     * - Deploying a new TokenVesting contract with the vesting period set
     * to the remaining time in the vesting period of the old contract
     * @param _oldTokenVesting The old TokenVesting contract
     */
    function migrate(TokenVesting _oldTokenVesting) external {
        // Use the same beneficiary address
        address beneficiary = _oldTokenVesting.beneficiary();

        // Calculate remaining time in vesting period
        uint256 oldStartTimestamp = _oldTokenVesting.start();
        uint256 oldVestingDuration = _oldTokenVesting.duration();
        uint256 oldEndTimestamp = oldStartTimestamp.add(oldVestingDuration);
        // The remaining vesting period duration for the old vesting contract
        // is the difference between the end timestamp for its vesting period
        // and the current block timestamp
        uint256 remainingVestingDuration = oldEndTimestamp.sub(block.timestamp);

        // Create new vesting contract
        TokenVesting newTokenVesting = create(beneficiary, remainingVestingDuration);

        emit TokenVestingMigrated(address(_oldTokenVesting), address(newTokenVesting));
    }

    /**
     * @dev Creates a vesting contract and transfers its ownership to the bank multisig
     * @param _beneficiary Address of the beneficiary to whom the vested tokens are transferred
     * @param _vestingDuration The duration in seconds of the period in which tokens will vest
     * @return A newly created TokenVesting contract
     */
    function create(
        address _beneficiary,
        uint256 _vestingDuration
    )
        public
        returns (TokenVesting)
    {
        // Use the current block timestamp as the start timestamp
        // for the new vesting contract
        uint256 startTimestamp = block.timestamp;

        TokenVesting tokenVesting = new TokenVesting(
            _beneficiary,
            startTimestamp,
            0, // No cliff is used
            _vestingDuration,
            true // All vesting contracts are revocable
        );

        tokenVesting.transferOwnership(bankMultisig);

        emit TokenVestingCreated(address(tokenVesting));

        return tokenVesting;
    }
}
