/**
 * @title Ownable
 * @dev The Ownable contract has an owner address, and provides basic authorization control
 * functions, this simplifies the implementation of "user permissions".
 */
contract Ownable {

    address public owner;

    /**
     * @dev The Ownable constructor sets the original `owner` of the contract to the sender
     * account.
     */
    constructor()public {
        owner = msg.sender;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }
}

/*
Cryptocasino http://playtowin.io - all rights reserved
Game drunkard
Game results are calculated using the service http://oraclize.it based on a query to the https://www.random.org/
The creators of the project are not responsible for any material losses during the game, all bets are made by you at your own peril and risk.
Contract address 0x7777776dd38fcf2a446297305981497f201f85ef, making a bet you give an announcement that you are of age and you are 18 years old.
*/
contract DrunkardGame is Ownable, usingOraclize {

    using SafeMath for uint;
    using strings for *;
    //drunkard settings
    address payable public marketing_address = 0x777777018285801412ec226229C6F6AE16445F89;
    string public constant GAME_DRUNKARD_TITLE = 'drunkard';
    uint public constant GAME_DRUNKARD_ID = 100;
    uint public constant MARKETING_PERCENT = 3;
    uint public constant MIN_STAKE = 0.05 ether;
    uint public constant MAX_STAKE = 1 ether;
    uint constant PERCENT_TO_WIN = 225; //bet+125%
    uint public round_drunkard = 0;
    uint custom_gas_limit;
    //drunkard saving games to history
    struct drunkard_game {
        address payable from;
        bytes32 queryId;
        uint    round;
        bool    winner;
        uint    bet;
        uint    choice;
        uint    game_choice;
        uint    timestamp;
        uint    profit;
        bool    status;
        uint    serial;
    }
    mapping (bytes32 => drunkard_game) drunkard_game_hystory;
    bytes32[] public oraclizedIndices;
    //incomplete user games
    mapping (address => uint) playerPendingWithdrawals;
    bool public drunkard_status;

    constructor()public{
        //set gas price to oraclize query
        oraclize_setCustomGasPrice(10000000000 wei);
        //set gas limit for oracle callback
        custom_gas_limit = 235000;
        //status game true: started, false: stopped
        drunkard_status = true;
    }

    event Drunkard(
        address indexed from,
        uint round,
        uint choice,
        uint bet,
        uint game_choice,
        bool winner,
        //uint timestamp,
        uint profit,
        bytes32 proof_hash
    );

    event Game(
        address indexed from,
        uint indexed block,
        uint value,
        string game,
        bytes32 proof_hash
    );

    //playing with contract wallets is prohibited
    modifier isNotContract() {
        uint size;
        address addr = msg.sender;
        assembly { size := extcodesize(addr) }
        require(size == 0 && tx.origin == msg.sender);
        _;
    }

    modifier drunkardIsStarted() {
        require(drunkard_status);
        _;
    }

    /*
    only if game is active & bet is valid can query oraclize and set player vars     
    */
    function game_drunkard(uint _choice)drunkardIsStarted isNotContract external payable {

        require(_choice >=1 && _choice <=3, "Invalid choice");
        require(msg.value >= MIN_STAKE && msg.value <= MAX_STAKE, 'Minimal stake 0.05 ether, max stake 1 ether');

        // Making oraclized query to random.org.
        bytes32 oraclizeQueryId;
        round_drunkard +=1;
        string memory queryString1 = "[URL] ['json(https://api.random.org/json-rpc/2/invoke).result.random[\"serialNumber\", \"data\", \"userData\"]', '\\n{\"jsonrpc\": \"2.0\", \"method\": \"generateSignedIntegers\", \"params\": { \"apiKey\": \"${[decrypt] BBrhDqQMkDMenw2av7Bl4vIPCmkF1jH+jj8R3vclZ8ar/Zi0jcvxdJ3cnOZquLE6+gxptFVY3RTZtjL/2hTppCA3dHCJpJ7KQHwWyfC9ZRkP94N/5LdNb4dLAtykAy2LZnHZGt1tSZOOjfndZZQzRgrvD9XF}\", \"n\": 1, \"min\": 1, \"max\": 3, \"userData\":";
        string memory queryString2 = uint2str(GAME_DRUNKARD_ID);
        string memory queryString3 = ",\"replacement\": true, \"base\": 10${[identity] \"}\"}, \"id\":";
        string memory queryString4 = uint2str(round_drunkard);
        string memory queryString5 = "${[identity] \"}\"}']";
        string memory queryString1_2 = queryString1.toSlice().concat(queryString2.toSlice());
        string memory queryString1_2_3 = queryString1_2.toSlice().concat(queryString3.toSlice());
        string memory queryString1_2_3_4 = queryString1_2_3.toSlice().concat(queryString4.toSlice());
        string memory queryString = queryString1_2_3_4.toSlice().concat(queryString5.toSlice());

        oraclizeQueryId = oraclize_query( 
          "nested", 
          queryString,
          custom_gas_limit
        ); 

        // Recording the bet info for future reference.
        drunkard_game_hystory[oraclizeQueryId].round = round_drunkard;
        drunkard_game_hystory[oraclizeQueryId].winner = false;
        drunkard_game_hystory[oraclizeQueryId].queryId = oraclizeQueryId;
        drunkard_game_hystory[oraclizeQueryId].from = msg.sender;
        drunkard_game_hystory[oraclizeQueryId].bet = msg.value;
        drunkard_game_hystory[oraclizeQueryId].choice = _choice;
        drunkard_game_hystory[oraclizeQueryId].status = true;

        // Recording oraclize indices.
        oraclizedIndices.push(oraclizeQueryId) -1;
        //Recording user bet
        playerPendingWithdrawals[msg.sender] = playerPendingWithdrawals[msg.sender].add(msg.value);
        emit Game(msg.sender, block.number, msg.value, GAME_DRUNKARD_TITLE, oraclizeQueryId);
    }

    /*only oracle can call this method*/
    function __callback(bytes32 myid, string memory result)public payable {
        require (msg.sender == oraclize_cbAddress(), 'Only an oracle can call a method.');

        if(drunkard_game_hystory[myid].status){
            /* map random result to player */
            strings.slice memory oraclize_result = result.toSlice();
            strings.slice memory part;
            //serial number of request for random.org
            uint serilal = parseInt(oraclize_result.split(",".toSlice(), part).toString()); 
            //random number from random.org
            uint choices = parseInt(oraclize_result.split(",".toSlice(), part).toString()); 
            //game ID
            uint game = parseInt(oraclize_result.split(",".toSlice(), part).toString());

            if(game == GAME_DRUNKARD_ID){
                __drunkard_result(myid, serilal, choices);
            }
        }
   }

   /*calculate winnings based on callback oraclize.io and random.org*/
   function __drunkard_result(bytes32 _id, uint _serial, uint _random)internal {
            address payable player = drunkard_game_hystory[_id].from;
            uint choice = drunkard_game_hystory[_id].choice;
            uint bet = drunkard_game_hystory[_id].bet;
            bool winner = false;
            uint profit;

            if(choice == _random){
                winner = true;
                //calculation of winnings: bet + 125% - 3% ccommission
                uint start_profit = bet.mul(PERCENT_TO_WIN).div(100);
                //system commission 3%
                uint commission = start_profit.mul(MARKETING_PERCENT).div(100);
                profit = start_profit - commission; 
                drunkard_game_hystory[_id].profit = profit;
                marketing_address.transfer(commission);
                player.transfer(profit);
            }else{
                drunkard_game_hystory[_id].profit = 0;
            }

            drunkard_game_hystory[_id].timestamp = now;
            drunkard_game_hystory[_id].game_choice = _random;
            drunkard_game_hystory[_id].winner = winner;
            drunkard_game_hystory[_id].status = false;
            drunkard_game_hystory[_id].serial = _serial;
            //delete the record of the pending user transaction
            playerPendingWithdrawals[player] = playerPendingWithdrawals[player].sub(bet);
            emit Drunkard(player, 1, choice, bet, _random, winner,  profit, _id);
   }

   /*get game specific data*/
   function getGame(bytes32 _game)public view returns(
       address from,
       bytes32 queryId,
       uint round,
       bool winner,
       uint bet,
       uint choice,
       uint game_choice,
       uint timestamp,
       uint profit,
       bool status,
       uint serial) {
        from = drunkard_game_hystory[_game].from;
        queryId = drunkard_game_hystory[_game].queryId;
        round = drunkard_game_hystory[_game].round;
        winner = drunkard_game_hystory[_game].winner;
        bet = drunkard_game_hystory[_game].bet;
        choice = drunkard_game_hystory[_game].choice;
        game_choice = drunkard_game_hystory[_game].game_choice;
        timestamp = drunkard_game_hystory[_game].timestamp;
        profit = drunkard_game_hystory[_game].profit;
        status = drunkard_game_hystory[_game].status;
        serial = drunkard_game_hystory[_game].serial;
    }

    /*
    All funds are on the contract and are not covered by the rate of cryptocasino property.
    The casino pays 0.0044 broadcasts at each bet per call by random.org.
    To cover the costs of maintaining servers and equipment, we must withdraw some of the money from the contract.
    */
    function returnRoyalty(uint _value) onlyOwner public {
        uint value = _value.mul(1 ether);
        if (address(this).balance >= value) {
            marketing_address.transfer(value);
        }
    }

    /* start game */
    function ownerStartGame() public onlyOwner {
        drunkard_status = true;
    }

    /* stop game */
    function ownerStopGame() public onlyOwner {
        drunkard_status = false;
    }

    /* set gas price for oraclize callback */
    function ownerSetCallbackGasPrice(uint _new_gas_price) public onlyOwner {
        oraclize_setCustomGasPrice(_new_gas_price);
    }

    /* set gas limit for oraclize query */
    function ownerSetOraclizeSafeGas(uint32 _new_custom_gas_limit) public onlyOwner	{
    	custom_gas_limit = _new_custom_gas_limit;
    }

     function() external payable {

    }
}
